// Normalization Logic
// Inputs: Items with 'source' ('PropStream' | 'Zillow') and raw data

const normalizedItems = items.map(item => {
  const json = item.json;
  const source = json.source || 'Unknown';

  // Initialize Canonical Object
  const deal = {
    source_system: source,
    last_data_refresh: new Date().toISOString(),
    properties: {},
    meta: {}
  };

  // Helper: Slugify for Key Generation
  const slugify = (text) => text ? text.toString().toLowerCase().trim().replace(/[^a-z0-9]+/g, '-') : '';

  if (source === 'PropStream') {
    // --- PROPSTREAM MAPPING ---
    const address = json['Address'] || '';
    const zip = json['Zip'] || '';

    deal.properties = {
      deal_key: `${slugify(address)}-${zip}`,
      address: address,
      city: json['City'],
      state: json['State'],
      zip: zip,
      property_type: json['Property Type'],
      beds: Number(json['Beds']),
      baths: Number(json['Baths']),
      sqft: Number(json['SqFt']),
      year_built: Number(json['Year Built']),
      estimated_arv: Number(json['Estimated Value']),
      equity_amt: Number(json['Equity Amount']),
      owner_name_1: json['Owner 1 First Name'] + ' ' + json['Owner 1 Last Name'],
      owner_phone: json['Owner Phone']
    };

  } else if (source.includes('Zillow')) {
    // --- ZILLOW MAPPING ---
    // Handle both 'Zillow' and 'Zillow Digest'
    const address = json.address || '';

    // CRITICAL FIX: Skip if address is not found (common in digests)
    if (!address || address === 'Address Not Found') {
        return null;
    }

    const zip = json.zipcode || '';

    deal.properties = {
      deal_key: `${slugify(address)}-${zip}`,
      address: address,
      city: json.city,
      state: json.state,
      zip: zip,
      property_type: json.homeType,
      beds: json.bedrooms,
      baths: json.bathrooms,
      sqft: json.livingArea,
      year_built: json.yearBuilt,
      estimated_arv: Number(json.price || 0), // Map price to ARV for now
      list_price: Number(json.price || 0),
      listing_url: json.listing_url || json.url,
      listing_status: json.homeStatus,
      zestimate: json.zestimate,
      description: json.description
    };
  }

  // --- CALCULATED FIELDS ---
  // Simple MAO (Maximum Allowable Offer) Logic: (ARV * 0.7) - Rehab(est 30k)
  const arv = deal.properties.estimated_arv || deal.properties.zestimate || 0;
  if (arv > 0) {
    deal.properties.mao = (arv * 0.70) - 30000;
  }

  return { json: deal };
});

// Filter out nulls (skipped items)
return normalizedItems.filter(item => item !== null);
